import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import './App.css';
import { Card, Icon, Modal} from 'antd';
import Nav from './Nav'
import {connect} from 'react-redux'

const { Meta } = Card;

function ScreenArticlesBySource(props) {

  const [articleList, setArticleList] = useState([])

  const [visible, setVisible] = useState(false)
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')

  const { id } = useParams();

  useEffect(() => {
    const findArticles = async() => {
      const data = await fetch(`https://newsapi.org/v2/top-headlines?sources=${id}&apiKey=9b113ef4af7b4581abd544e9c761ab74`)
      const body = await data.json()
      console.log(body)
      setArticleList(body.articles) 
    }

    findArticles()    
  },[])


  // Enregistrer un article
  const saveWishlist = async (article) => {
    console.log('saveWishlist')

    props.addToWishList(article);

    const saveResponse = await fetch('/wishlist-article', {
      method: 'POST',
      headers: {'Content-Type': 'application/x-www-form-urlencoded'},
      body: `name=${article.title}&content=${article.content}&desc=${article.description}&img=${article.urlToImage}&token=${props.token}&lang=${props.selectLanguage}`
    })
  };  

  console.log('props.token', props.token)

  const showModal = (title, content) => {
    setVisible(true)
    setTitle(title)
    setContent(content)

  }

  const handleOk = e => {
    console.log(e)
    setVisible(false)
  }

  const handleCancel = e => {
    console.log(e)
    setVisible(false)
  }

  return (
    <div>
      <Nav/>
      <div className="Banner"/>
      <div className="Card">
        {articleList.map((article,i) => (
          <div key={i} style={{display:'flex',justifyContent:'center'}}>
            <Card
              style={{ 
                width: 300, 
                margin:'15px', 
                display:'flex',
                flexDirection: 'column',
                justifyContent:'space-between' 
              }}
              cover={ 
                <img
                  alt="example"
                  src={article.urlToImage}
                />
              }
              actions={[
                <Icon type="read" key="ellipsis2" onClick={() => showModal(article.title,article.content)} />,
                <Icon type="like" key="ellipsis" onClick={()=> {saveWishlist(article)}} />
              ]}
            >
            <Meta
              title={article.title}
              description={article.description}
            />
            </Card>
            <Modal
              title={title}
              visible={visible}
              onOk={handleOk}
              onCancel={handleCancel}
            >
              <p>{content}</p>
            </Modal>
          </div>
        ))}
      </div> 
    </div>
  );
}


function mapStateToProps(state) {
  return {
      token: state.token
  }
};

function mapDispatchToProps(dispatch){
  return {
    addToWishList: function(article){
      dispatch({type: 'addArticle',
                articleLiked: article
      })
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ScreenArticlesBySource)
